import {getDatabase , ref , set} from "firebase/database";
import {getAuth, GoogleAuthProvider , signInWithPopup } from "firebase/auth";
import {app} from "./Firebase";
import  "./Login.css";

const auth = getAuth();
const db = getDatabase(app);
const provider = new GoogleAuthProvider();


const signupWithGoogle = ()=>{
    signInWithPopup(auth , provider);
}

function Login() {
     const signin = ()=>{
        set((ref(db , "nadir/user")), {
            id : 1,
            name:"nadir",
            age:21,
        });
     }
     
    
  return (
    
    <div className='login'>
     
      <div>
      <img src='https://www.facebook.com/images/fb_icon_325x325.png' alt='facebook logo'/>
      <img src='https://logodownload.org/wp-content/uploads/2014/09/facebook-logo-15.png' alt='Facebook logo text'/>
      </div>
      <button type='submit' onClick={signin} >Sign In</button>
      <button type='submit' onClick={signupWithGoogle} >Sign In</button>
      
    
    </div>
  )
}

export default Login;
